                                        $diagnosticsLogCategoriesNotCoveredByPolicy = 'n/a'
                                        $recommendation = 'no recommendation as this resourceType seems not existing'
                                        $logsSupported = 'unknown'
                                    }

                                    $null = $diagnosticsPolicyAnalysis.Add([PSCustomObject]@{
