#>

[CmdletBinding()]
Param
(
    [string]$Product = "AzGovViz",
    [string]$ProductVersion = "v6_major_20220116_2",
    [string]$GithubRepository = "aka.ms/AzGovViz",
    [string]$ManagementGroupId,
    [switch]$AzureDevOpsWikiAsCode, #deprecated - Based on environment variables the script will detect the code run platform
    [switch]$DebugAzAPICall,
    [switch]$NoCsvExport,
    [string]$CsvDelimiter = ";",
    [switch]$CsvExportUseQuotesAsNeeded,
    [string]$OutputPath,
    [switch]$DoNotShowRoleAssignmentsUserData,
    [switch]$HierarchyMapOnly,
    [switch]$NoASCSecureScore,
    [switch]$NoMDfCSecureScore,
    [switch]$NoResourceProvidersDetailed,
    [int]$LimitCriticalPercentage = 80,
    [array]$SubscriptionQuotaIdWhitelist = @("undefined"),
    [switch]$NoPolicyComplianceStates,
    [switch]$NoResourceDiagnosticsPolicyLifecycle,
    [switch]$NoAADGroupsResolveMembers,
    [int]$AADServicePrincipalExpiryWarningDays = 14,
    [switch]$NoAzureConsumption, #obsolete
    [switch]$DoAzureConsumption,
    [int]$AzureConsumptionPeriod = 1,
    [switch]$NoAzureConsumptionReportExportToCSV,
    [switch]$DoTranscript,
    [int]$HtmlTableRowsLimit = 20000, #HTML TenantSummary may become unresponsive depending on client device performance. A recommendation will be shown to use the CSV file instead of opening the TF table
    [int]$ThrottleLimit = 10,
    [array]$ExludedResourceTypesDiagnosticsCapable = @("microsoft.web/certificates"),
    [switch]$DoNotIncludeResourceGroupsOnPolicy,
    [switch]$DoNotIncludeResourceGroupsAndResourcesOnRBAC,
    [parameter(ValueFromPipeline)][ValidateSet("TD", "LR")][string]$AzureDevOpsWikiHierarchyDirection = "TD",
    [string]$SubscriptionId4AzContext = "undefined",
    [int]$ChangeTrackingDays = 14,
    [string]$FileTimeStampFormat = "yyyyMMdd_HHmmss",
    [switch]$NoJsonExport,
    [switch]$JsonExportExcludeResourceGroups,
    [switch]$JsonExportExcludeResources,
    [switch]$LargeTenant,
    [switch]$NoScopeInsights,
    [int]$AADGroupMembersLimit = 500,
    [switch]$PolicyAtScopeOnly,
    [switch]$RBACAtScopeOnly,
    [switch]$NoResources,
    [switch]$StatsOptOut,
    [switch]$NoSingleSubscriptionOutput,
    [switch]$ManagementGroupsOnly,

    #https://docs.microsoft.com/en-us/azure/azure-resource-manager/management/azure-subscription-service-limits#role-based-access-control-limits
    [int]$LimitRBACCustomRoleDefinitionsTenant = 5000,
    [int]$LimitRBACRoleAssignmentsManagementGroup = 500,
    [int]$LimitRBACRoleAssignmentsSubscription = 2000,
    #[string]$LimitRBACRoleAssignmentsSubscription = 2000 #will be retrieved programatically

    #https://docs.microsoft.com/en-us/azure/governance/policy/overview#maximum-count-of-azure-policy-objects
    [int]$LimitPOLICYPolicyAssignmentsManagementGroup = 200,
    [int]$LimitPOLICYPolicyAssignmentsSubscription = 200,
    [int]$LimitPOLICYPolicyDefinitionsScopedManagementGroup = 500,
    [int]$LimitPOLICYPolicyDefinitionsScopedSubscription = 500,
    [int]$LimitPOLICYPolicySetAssignmentsManagementGroup = 200,
    [int]$LimitPOLICYPolicySetAssignmentsSubscription = 200,
    [int]$LimitPOLICYPolicySetDefinitionsScopedTenant = 2500,
    [int]$LimitPOLICYPolicySetDefinitionsScopedManagementGroup = 200,
    [int]$LimitPOLICYPolicySetDefinitionsScopedSubscription = 200,

    #https://docs.microsoft.com/en-us/azure/azure-resource-manager/management/azure-subscription-service-limits#subscription-limits
    [int]$LimitResourceGroups = 980,
    [int]$LimitTagsSubscription = 50
)

$Error.clear()
$ErrorActionPreference = "Stop"
#removeNoise
$ProgressPreference = 'SilentlyContinue'
Set-Item Env:\SuppressAzurePowerShellBreakingChangeWarnings "true"

#region CheckCodeRunPlatform
if ($env:GITHUB_SERVER_URL -and $env:CODESPACES) {
    #GitHub Codespaces
    $checkCodeRunPlatform = "GitHubCodespaces"
}
elseif ($env:SYSTEM_TEAMPROJECTID -and $env:BUILD_REPOSITORY_ID) {
    #Azure DevOps
    $checkCodeRunPlatform = "AzureDevOps"
    $onAzureDevOps = $true
    $onAzureDevOpsOrGitHubActions = $true
}
elseif ($PSPrivateMetadata) {
    #Azure Automation
    $checkCodeRunPlatform = "AzureAutomation"
}
elseif ($env:GITHUB_ACTIONS) {
    #GitHub Actions
    $checkCodeRunPlatform = "GitHubActions"
    $onAzureDevOpsOrGitHubActions = $true
}
elseif ($env:ACC_IDLE_TIME_LIMIT -and $env:AZURE_HTTP_USER_AGENT -and $env:AZUREPS_HOST_ENVIRONMENT) {
    #Azure Cloud Shell
    $checkCodeRunPlatform = "CloudShell"
}
else {
    #Other Console
    $checkCodeRunPlatform = "Console"
}
#endregion CheckCodeRunPlatform

#region file
if (-not [IO.Path]::IsPathRooted($outputPath)) {
    $outputPath = Join-Path -Path (Get-Location).Path -ChildPath $outputPath
}
$outputPath = Join-Path -Path $outputPath -ChildPath '.'
$outputPath = [IO.Path]::GetFullPath($outputPath)
if (-not (test-path $outputPath)) {
    Write-Host "path $outputPath does not exist - please create it!" -ForegroundColor Red
    Throw "Error - AzGovViz: check the last console output for details"
}
else {
    Write-Host "Output/Files will be created in path $outputPath"
}
$DirectorySeparatorChar = [IO.Path]::DirectorySeparatorChar

#fileTimestamp
try {
    $fileTimestamp = (Get-Date -Format $FileTimeStampFormat)
}
catch {
    Write-Host "fileTimestamp format: '$($FileTimeStampFormat)' invalid; continue with default format: 'yyyyMMdd_HHmmss'" -ForegroundColor Red
    $FileTimeStampFormat = "yyyyMMdd_HHmmss"
    $fileTimestamp = (Get-Date -Format $FileTimeStampFormat)
}

#region DoTranscript
if ($DoTranscript) {
    if ($ManagementGroupId) {
        if ($onAzureDevOpsOrGitHubActions -eq $true) {
            if ($HierarchyMapOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_HierarchyMapOnly_$($ManagementGroupId)_Log.txt"
            }
            elseif ($ManagementGroupsOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_ManagementGroupsOnly_$($ManagementGroupId)_Log.txt"
            }
            else {
                $fileNameTranscript = "AzGovViz_$($ManagementGroupId)_Log.txt"
            }
        }
        else {
            if ($HierarchyMapOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_HierarchyMapOnly_$($ProductVersion)_$($fileTimestamp)_$($ManagementGroupId)_Log.txt"
            }
            elseif ($ManagementGroupsOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_ManagementGroupsOnly_$($ProductVersion)_$($fileTimestamp)_$($ManagementGroupId)_Log.txt"
            }
            else {
                $fileNameTranscript = "AzGovViz_$($ProductVersion)_$($fileTimestamp)_$($ManagementGroupId)_Log.txt"
            }
        }
    }
    else {
        if ($onAzureDevOpsOrGitHubActions -eq $true) {
            if ($HierarchyMapOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_HierarchyMapOnly_Log.txt"
            }
            elseif ($ManagementGroupsOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_ManagementGroupsOnly_Log.txt"
            }
            else {
                $fileNameTranscript = "AzGovViz_Log.txt"
            }
        }
        else {
            if ($HierarchyMapOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_HierarchyMapOnly_$($ProductVersion)_$($fileTimestamp)_Log.txt"
            }
            elseif ($ManagementGroupsOnly -eq $true) {
                $fileNameTranscript = "AzGovViz_ManagementGroupsOnly_$($ProductVersion)_$($fileTimestamp)_Log.txt"
            }
            else {
                $fileNameTranscript = "AzGovViz_$($ProductVersion)_$($fileTimestamp)_Log.txt"
            }
        }
    }
    Write-Host "Writing transcript: $($outputPath)$($DirectorySeparatorChar)$($fileNameTranscript)"
    Start-Transcript -Path "$($outputPath)$($DirectorySeparatorChar)$($fileNameTranscript)"
}
#endregion DoTranscript

#endregion file

#time
$executionDateTimeInternationalReadable = Get-Date -Format "dd-MMM-yyyy HH:mm:ss"
$currentTimeZone = (Get-TimeZone).Id

#start
$startAzGovViz = Get-Date
$startTime = Get-Date -Format "dd-MMM-yyyy HH:mm:ss"
$startTimeUTC = ((Get-Date).ToUniversalTime()).ToString("dd-MMM-yyyy HH:mm:ss")
Write-Host "Start AzGovViz $($startTime) (#$($ProductVersion))"
Write-Host "CheckCodeRunPlatform: running in $($checkCodeRunPlatform)"

if ($DebugAzAPICall) {
    write-host "AzAPICall debug enabled" -ForegroundColor Cyan
}
else {
    write-host "AzAPICall debug disabled" -ForegroundColor Cyan
}

#region ChinaBilling
$checkContext = Get-AzContext -ErrorAction Stop
Write-Host "Environment: $($checkContext.Environment.Name)"
if ($DoAzureConsumption) {
    #cloudEnvironment specific
    if ($checkContext.Environment.Name -eq "AzureChinaCloud") {
        Write-Host "Azure Billing not supported in AzureChinaCloud, skipping Consumption.."
        $DoAzureConsumption = $false
    }
}
#endregion ChinaBilling

#region htParameters (all switch params used in foreach-object -parallel)
if ($LargeTenant -eq $true) {
    $NoScopeInsights = $true
    $NoResourceProvidersDetailed = $true
    $PolicyAtScopeOnly = $true
    $RBACAtScopeOnly = $true
}

if ($ManagementGroupsOnly) {
    $NoSingleSubscriptionOutput = $true
}

if ($HierarchyMapOnly) {
    $NoJsonExport = $true
}

if ($NoASCSecureScore -or $NoMDfCSecureScore) {
    $NoMDfCSecureScore = $true
}

$htParameters = @{
    ProductVersion                               = $ProductVersion
    AzCloudEnv                                   = $checkContext.Environment.Name
    GithubRepository                             = $GithubRepository
    onAzureDevOps                                = [bool]$onAzureDevOps
    onAzureDevOpsOrGitHubActions                 = [bool]$onAzureDevOpsOrGitHubActions
    DebugAzAPICall                               = [bool]$DebugAzAPICall
    DoNotShowRoleAssignmentsUserData             = [bool]$DoNotShowRoleAssignmentsUserData
    HierarchyMapOnly                             = [bool]$HierarchyMapOnly
    NoMDfCSecureScore                            = [bool]$NoMDfCSecureScore
    PolicyAtScopeOnly                            = [bool]$PolicyAtScopeOnly
    RBACAtScopeOnly                              = [bool]$RBACAtScopeOnly
    NoResourceProvidersDetailed                  = [bool]$NoResourceProvidersDetailed
    NoPolicyComplianceStates                     = [bool]$NoPolicyComplianceStates
    DoNotIncludeResourceGroupsOnPolicy           = [bool]$DoNotIncludeResourceGroupsOnPolicy
    DoNotIncludeResourceGroupsAndResourcesOnRBAC = [bool]$DoNotIncludeResourceGroupsAndResourcesOnRBAC
    LargeTenant                                  = [bool]$LargeTenant
    NoJsonExport                                 = [bool]$NoJsonExport
    NoResources                                  = [bool]$NoResources
    DoAzureConsumption                           = [bool]$DoAzureConsumption
    ManagementGroupsOnly                         = [bool]$ManagementGroupsOnly
}
#endregion htParameters

#region PowerShellEditionAnVersionCheck
Write-Host "Checking powershell edition and version"
$requiredPSVersion = "7.0.3"
$splitRequiredPSVersion = $requiredPSVersion.split('.')
$splitRequiredPSVersionMajor = $splitRequiredPSVersion[0]
$splitRequiredPSVersionMinor = $splitRequiredPSVersion[1]
$splitRequiredPSVersionPatch = $splitRequiredPSVersion[2]

$thisPSVersion = ($PSVersionTable.PSVersion)
$thisPSVersionMajor = ($thisPSVersion).Major
$thisPSVersionMinor = ($thisPSVersion).Minor
$thisPSVersionPatch = ($thisPSVersion).Patch

$psVersionCheckResult = "letsCheck"

if ($PSVersionTable.PSEdition -eq "Core" -and $thisPSVersionMajor -eq $splitRequiredPSVersionMajor) {
    if ($thisPSVersionMinor -gt $splitRequiredPSVersionMinor) {
        $psVersionCheckResult = "passed"
        $psVersionCheck = "(Major[$splitRequiredPSVersionMajor]; Minor[$thisPSVersionMinor] gt $($splitRequiredPSVersionMinor))"
    }
    else {
        if ($thisPSVersionPatch -ge $splitRequiredPSVersionPatch) {
            $psVersionCheckResult = "passed"
            $psVersionCheck = "(Major[$splitRequiredPSVersionMajor]; Minor[$splitRequiredPSVersionMinor]; Patch[$thisPSVersionPatch] gt $($splitRequiredPSVersionPatch))"
        }
        else {
            $psVersionCheckResult = "failed"
            $psVersionCheck = "(Major[$splitRequiredPSVersionMajor]; Minor[$splitRequiredPSVersionMinor]; Patch[$thisPSVersionPatch] lt $($splitRequiredPSVersionPatch))"
        }
    }
}
else {
    $psVersionCheckResult = "failed"
    $psVersionCheck = "(Major[$splitRequiredPSVersionMajor] ne $($splitRequiredPSVersionMajor))"
}

if ($psVersionCheckResult -eq "passed") {
    Write-Host " PS check $psVersionCheckResult : $($psVersionCheck); (minimum supported version '$requiredPSVersion')"
    Write-Host " PS Edition: $($PSVersionTable.PSEdition)"
    Write-Host " PS Version: $($PSVersionTable.PSVersion)"
}
else {
    Write-Host " PS check $psVersionCheckResult : $($psVersionCheck)"
    Write-Host " PS Edition: $($PSVersionTable.PSEdition)"
    Write-Host " PS Version: $($PSVersionTable.PSVersion)"
    Write-Host " This AzGovViz version only supports Powershell 'Core' version '$($requiredPSVersion)' or higher"
    Write-Host " Get Powershell: https://github.com/PowerShell/PowerShell#get-powershell"
    Write-Host " Installing PowerShell on Windows: https://docs.microsoft.com/en-us/powershell/scripting/install/installing-powershell-core-on-windows"
    Write-Host " Installing PowerShell on Linux: https://docs.microsoft.com/en-us/powershell/scripting/install/installing-powershell-core-on-linux"
    Throw "Error - AzGovViz: check the last console output for details"
}
#endregion PowerShellEditionAnVersionCheck

