            $uri = "$(($htAzureEnvironmentRelatedUrls).($checkContext.Environment.Name).MSGraphUrl)/v1.0/directoryObjects/getByIds"
            $method = "POST"
            $body = @"
        {
            "ids":[$($nonResolvedIdentitiesToCheck)]
