                        $null = $script:resourceTypesDiagnosticsArray.Add([PSCustomObject]@{
                                ResourceType  = $resourcetype
                                Metrics       = $metrics
                                Logs          = $logs
                                LogCategories = $logCategories
                                ResourceCount = $resourceCount
                            })
                    }
                }
                else {
