                "statsParametersRBACAtScopeOnly": "$($htParameters.RBACAtScopeOnly)",
                "statsTry": "$($tryCounter)"
            }
        }
    }
}
"@
            $stats = Invoke-WebRequest -Uri 'https://dc.services.visualstudio.com/v2/track' -Method 'POST' -body $statusBody
